document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".menu-item-category");e.forEach(e=>{const t=e.querySelector(".submenu");if(t){t.style.display="none";const n=document.createElement("span");n.className="category-toggle",n.innerHTML="▶",n.style.marginRight="5px",n.style.cursor="pointer",n.style.fontSize="0.8em";const s=e.querySelector("a");s.parentNode.insertBefore(n,s),n.addEventListener("click",function(e){e.preventDefault(),e.stopPropagation(),t.style.display==="none"?(t.style.display="block",n.innerHTML="▼"):(t.style.display="none",n.innerHTML="▶")})}})})