document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".menu-item-section");e.forEach(e=>{const t=e.querySelector(".submenu");if(t){const s=t.querySelector(".active")!==null;t.style.display=s?"block":"none";const n=document.createElement("span");n.className="section-toggle",n.innerHTML=s?"▼":"▶",n.style.marginRight="5px",n.style.cursor="pointer",n.style.fontSize="0.8em",n.style.display="inline-block",n.style.width="12px";const o=e.querySelector("a");o.parentNode.insertBefore(n,o),n.addEventListener("click",function(e){e.preventDefault(),e.stopPropagation(),t.style.display==="none"?(t.style.display="block",n.innerHTML="▼"):(t.style.display="none",n.innerHTML="▶")})}})})